// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ChessPlayed {
    enum GameState {
        NotStarted,
        Started,
        Draw,
        Resign,
        Win
    }

    ChessPlayed public chess;

    GameState public currentState;
    address payable public player1;
    address payable public player2;
    uint256 public betAmount;

    constructor() payable {}

    function startGame(address player1, address player2) public payable returns(bool, uint256) {
        require(currentState == GameState.NotStarted, "Game already started");
        require(msg.value > 0, "Bet amount should be greater than 0");
        require(msg.sender != player1, "You cannot play with yourself");

        player1 = payable(msg.sender);
        player2 = payable(msg.sender == msg.sender ? address(this) : msg.sender); 
        betAmount = msg.value;
        currentState = GameState.Started;
        return (true, betAmount);
    }

    function draw() public {
        require(currentState == GameState.Started, "Game not started yet");
        require(msg.sender == player1 || msg.sender == player2, "You are not a player");

        currentState = GameState.Draw;
    }

    function resign() public {
        require(currentState == GameState.Started, "Game not started yet");
        require(msg.sender == player1 || msg.sender == player2, "You are not a player");
        payable(msg.sender).transfer(betAmount);
        currentState = GameState.Resign; 
    }

    function win() public {
        require(currentState == GameState.Started, "Game not started yet");
        currentState = GameState.Win;
    }

    receive() external payable {}

}

